const boton = document.getElementById('back-to-top');

window.onscroll = function () {
  var y = window.scrollY;
  if (y > 500) {
    boton.classList.add('back-to-top-ap');
  } else {
    boton.classList.remove('back-to-top-ap');
  }
};

function setClipboardCard(){
    var id = "referir";
    var existsTextarea = document.getElementById(id);

    existsTextarea.removeAttribute("disabled");

    existsTextarea.select();
    
    document.execCommand('copy');

    existsTextarea.setAttribute("disabled", "true");
    
    Materialize.toast('<i class="mdi-content-content-copy green-text" style="margin-right: 5px;"></i> <h6 class="green-text">Enlace Copiado</h6>', 4000);

}

